typedef struct {
	int x;
	int y;
} Point;

typedef struct
{
    uint16_t i;
    uint16_t j;

} Cell;
unsigned char buffer [16];

void draw_char(Point p, unsigned char ascii) {
	GetASCIICode(0, buffer, ascii);
	for (int itery = p.y; itery < p.y + 16; ++itery) {
		for (int iterx = p.x; iterx < p.x + 8; ++iterx) {
			int i = 1 << (p.x + 7 - iterx); //
			if (i & buffer[itery - p.y]) {
				lcdWriteReg(ADRX_RAM, iterx);
				lcdWriteReg(ADRY_RAM, itery);
				lcdWriteReg(DATA_RAM, 0xFFFF );
			}
		}
	}
}

void draw_string(Point p, const char* str) {
	while (*str) {
		draw_char(p, *str);
		
		p.x += 8;
		if (p.x >= 240 - 16) {
			p.x = 8;
			p.y += 20;
		}
		str++;
	}
}

void clearScreen(uint16_t color) {
	lcdWriteReg(ADRX_RAM, 0);
	lcdWriteReg(ADRY_RAM, 0);
	lcdWriteIndex(DATA_RAM);
	for (int itery = 0; itery < 320; ++itery) {
		for (int iterx = 0; iterx < 240; ++iterx) {
			lcdWriteData(color);
		}
	}
}
void drawSquare(uint32_t x,uint32_t y){
		for(int i=0;i<10;i++){
			int	pixel_x=i+x;
			for(int j=0;j<10;j++){
				int pixel_y=j+y;
				lcdWriteReg(ADRX_RAM,pixel_x);
				lcdWriteReg(ADRY_RAM,pixel_y);
				lcdWriteReg(DATA_RAM,LCDWhite);
				}
		}
	}

void fillCoord(Cell p, uint16_t color){
	
    for (int i = 0; i < GRID_SIZE; i++)
    {
        int pixel_x = i + p.i * GRID_SIZE;
        for (int j = 0; j < GRID_SIZE; j++)
        {
            int pixel_y = j + p.j * GRID_SIZE;
             lcdWriteReg(ADRX_RAM, pixel_x);
             lcdWriteReg(ADRY_RAM, pixel_y);
             lcdWriteReg(DATA_RAM, color);
        }//square to jeden segment weza, jedzenia itp 10x10 pixeli
    }
}


