int main(){
	initialize();

	clearScreen(GRASS_COLOR);
	
	 Cell snake;
   Cell food;
   int16_t X[MAX_SNAKE_SIZE], Y[MAX_SNAKE_SIZE];
   uint16_t length = 5;
   int16_t d = JOYSTICK_UP;
	
    //startsanke
   X[0] = BOARD_X / 2;
   Y[0] = BOARD_Y / 2;
    for (int i = 0; i < length; i++)
    {
        X[i] = X[0] - i;
        Y[i] = Y[0];
        snake.i = X[i];
        snake.j = Y[i];

			if (i == 0)
            fillCoord(snake, HEAD_COLOR);
        else
            fillCoord(snake, SNAKE_COLOR);
    }

    uint16_t tempx;
    uint16_t tempy;
    bool tof = false;
    while (!tof)
    {
        tempx = ((rand() % (91)) * X[length - 1]) % (BOARD_X);
        tempy = ((rand() % (91)) * Y[length - 1]) % (BOARD_Y);
        for (int i = 0; i < length; i++)
            if ((tempx != X[i]) && (tempy != Y[i]))
                tof = true;
    }

    food.i = tempx;
    food.j = tempy;
    fillCoord(food, FOOD_COLOR);

    wait_delay(2000);
		
		    uint16_t dir;
		
    while (1)
    {

        // updateSnake
        snake.i = X[length - 1];
        snake.j = Y[length - 1];
        fillCoord(snake, GRASS_COLOR);

        //przesuniecie poza glowa
        for (int i = length - 1; i > 0; i--)
        {
            X[i] = X[i - 1];
            Y[i] = Y[i - 1];
        }

        //przesuniecie glowy
        if (d == JOYSTICK_LEFT)
            Y[0] -= 1;
        else if (d == JOYSTICK_UP)
            X[0] += 1;
        else if (d == JOYSTICK_RIGHT)
            Y[0] += 1;
        else if (d == JOYSTICK_DOWN)
            X[0] -= 1;
        if ((X[0] < 0) || (X[0] >= BOARD_X) || (Y[0] < 0) || (Y[0] >= BOARD_Y)){
					LPC_GPDMACH0->DMACCConfig = 0;
					musicStop();
            break;} //GAME OVER 
 
						dir = Joystick_GetState();
            if ((dir == JOYSTICK_RIGHT) && (d != JOYSTICK_LEFT))
                d = JOYSTICK_RIGHT;
            else if ((dir == JOYSTICK_UP) && (d != JOYSTICK_DOWN))
                d = JOYSTICK_UP;
            else if ((dir == JOYSTICK_LEFT) && (d != JOYSTICK_RIGHT))
                d = JOYSTICK_LEFT;
            else if ((dir == JOYSTICK_DOWN) && (d != JOYSTICK_UP))
                d = JOYSTICK_DOWN;

        //zjadl 
        if ((X[0] == food.i) && (Y[0] == food.j))
        {
            tof = false;
            while (!tof)
            {
                tempx = rand() % (BOARD_X);
                tempy = rand() % (BOARD_Y);
                for (int i = 0; i < length; i++)
                    if ((tempx != X[i]) && (tempy != Y[i]))
                        tof = true;
            }

            food.i = tempx;
            food.j = tempy;
            fillCoord(food, FOOD_COLOR);
            length += 1;
        }

        for (int i = 0; i < length-1; i++)
        {
            snake.i = X[i];
            snake.j = Y[i];
            if(i == 0)
                fillCoord(snake, HEAD_COLOR);
            else
                fillCoord(snake, SNAKE_COLOR);
        }
        wait_delay(2000000-1000*length);
    }

	
}
